import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { Button, List, Card, Icon } from 'antd';
import NicknameEditForm from '../components/NicknameEditForm';
import { LOAD_FOLLOWERS_REQUEST, LOAD_FOLLOWINGS_REQUEST } from '../reducers/user';
import { LOAD_USER_POST_REQUEST } from '../reducers/post';

const Profile = () => {
    const dispatch = useDispatch();
    const { me } = useSelector(state => state.user);
    useEffect(() => {
        dispatch({
            type : LOAD_FOLLOWERS_REQUEST,
            data : me.id,
        });
        dispatch({
            type : LOAD_FOLLOWINGS_REQUEST,
            data : me.id,
        })
        dispatch({
            type : LOAD_USER_POST_REQUEST,
        })
    })

    return(
        <>
            <div style = {{margin : '40px 0 0'}}>
                <NicknameEditForm/>
                <List
                    style = {{marginBottom : '20px'}}
                    grid = {{gutter : 4, xs : 2, md : 3}}
                    size = "small"
                    header = {<div> Follower List</div>}
                    loadMore = {<Button style={{width : '100%'}}>More</Button>}
                    bordered
                    dataSource = {['JINNE', '25', 'FRONTEND']}
                    renderItem = {item => (
                        <List.Item style = {{marginTop : '20px'}}>
                            <Card actions = {[<Icon key="stop" type="stop" />]}>
                                <Card.Meta description={item}/>
                            </Card>
                        </List.Item>
                    )}
                />
                <List
                    style = {{marginBottom : '20px'}}
                    grid = {{gutter : 4, xs : 2, md : 3}}
                    size = "small"
                    header = {<div> Following List</div>}
                    loadMore = {<Button style={{width : '100%'}}>More</Button>}
                    bordered
                    dataSource = {['JINNE', '25', 'FRONTEND']}
                    renderItem = {item => (
                        <List.Item style = {{marginTop : '20px'}}>
                            <Card actions = {[<Icon key="stop" type="stop" />]}>
                                <Card.Meta description={item}/>
                            </Card>
                        </List.Item>
                    )}
                />
            </div>
        </>
    );
};

export default Profile;
