import React from 'react';
import { Form, Input, Button } from 'antd';
import { useSelector } from 'react-redux';

const PostForm = () => {
  const { imagePaths } = useSelector(state => state.post);
    return (
        <Form style={{margin : '40px 0 20px'}} encType = "multipart/form-data">
            <Input.TextArea maxLength = {140} placeholder = "HOW ARE YOU TODAY?"/>
            <div>
              <Input type = "file" multiple hidden />
              <Button>Image Upload</Button>
              <Button type = "primary" style = {{ float : "right" }} htmlType = "submit">TWIT!</Button>
            </div>
            <div>
              {imagePaths.map((v, i) => {
                return (
                  <div key = {v} style = {{display : 'inline-block'}}>
                    <img src={'http://localhost : 3000/' + v} stype={{width : '200px'}} alt = {v}/>
                    <div>
                      <Button>REMOVE</Button>
                    </div>
                  </div>
                )
              })}
            </div>
        </Form>
    )
};

export default PostForm;
