import React, { useCallback } from 'react';
import { Card, Avatar, Button } from 'antd';
import { useSelector, useDispatch } from 'react-redux';
import { logoutAction } from '../reducers/user';

const UserProfile = () => {
    const { user } = useSelector(state => state.user)
    const dispatch = useDispatch();
    const onLogout = useCallback(() => {
        dispatch(logoutAction);
    }, []);
    let postLength = user && user.Posts ? user.Posts.length : 0;
    let followingLength = user && user.Followings ? user.Followings.length : 0;
    let followerLength = user && user.Followers ? user.Followers.length : 0;
    return (
        <Card actions = {[
            <div key="twit">짹짹<br/>{postLength}</div>,
            <div key="following">팔로윙<br/>{followingLength}</div>,
            <div key="follower">팔로워<br/>{followerLength}</div>
        ]}>
        <Card.Meta
            avatar = {<Avatar>{user.nickname[0]}</Avatar>}
            title = {user.nickname}
        />
        <Button onClick = {onLogout}>LOGOUT</Button>
        </Card>
    )
}

export default UserProfile;
