const addPost = {
    type : ADD_POST,
}

const addDummy = {
    type : ADD_DUMMY,
    data : {
        content : 'hello!',
        UserId : 1,
        User : {
            nickname : 'JINNE',
        },
    },
}

const reducer = (state = initialState, action) => {
    switch (action.type){
        case ADD_POST : {
            return {
                ...state,
            }
        }
        case ADD_DUMMY : {
            return {
                ...state,
                mainPosts : [action.data, ...state.mainPosts],
            }
        }
        default : {
            return {
                ...state,
            }
        }
    }
}

export default reducer;
