import React from 'react';
import { Avatar, Card, Input, Button, Icon, Form } from 'antd';

const dummy = {
  isLoggedIn : true,
  imagePaths : [],
  mainPosts : [{
    User : {
      Id : 1,
      nickname : 'JINNE',
    },
    content : '>_<',
  }],
}

const Home = () => {
  return (
    <>
        <div>
          {dummy.isLoggedIn && <Form encType = "multipart/form-data">
            <Input.TextArea maxLength = {140} placeholder = "HOW ARE YOU TODAY?"/>
            <div>
              <Input type = "file" multiple hidden />
              <Button>Image Upload</Button>
              <Button type = "primary" style = {{ float : "right" }} htmlType = "submit">TWIT!</Button>
            </div>
            <div>
              {dummy.imagePaths.map((v, i) => {
                return (
                  <div key = {v} style = {{display : 'inline-block'}}>
                    <img src={'http://localhost : 3000/' + v} stype={{width : '200px'}} alt = {v}/>
                    <div>
                      <Button>REMOVE</Button>
                    </div>
                  </div>
                )
              })}
            </div>
          </Form>}
          {dummy.mainPosts.map((c) => {
            return (
              <Card
                style = {{margin : '40px 0 0'}}
                key = {+c.createdAT}
                cover = {c.img && <img alt="example" src={c.img}/>}
                actions = {[
                    <Icon type="retweet" key="retweet"/>,
                    <Icon type="heart" key="heart"/>,
                    <Icon type="message" key="message" />,
                    <Icon type="ellipsis" key="ellipsis"/>
                ]}
                extra = {<Button>follow</Button>}
              >
              <Card.Meta
                  avatar = {<Avatar>{c.User.nickname[0]}</Avatar>}
                  title = {c.User.nickname}
                  description = {c.content}
                />
              </Card>
            )
          })}
        </div>
    </>
  );
};

export default Home;
