import React, { useEffect } from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
// import LoginForm from './LoginForm';
import { Menu, Input, Row, Col, Card, Avatar, Button, Form } from 'antd';
// import UserProfile from './UserProfile';
// import { useSelector, useDispatch } from 'react-redux';
// import { LOAD_USER_REQUEST } from '../reducers/user';

// 나중에 지울 부분 dummy
const dummy = {
    nickname : 'JINNE',
    Post : [],
    Followings : [],
    Followers : [],
    isLoggedIn : false,
}
// 나중에 지울 부분 dummy

const AppLayout = (props) => {
    // const { me } = useSelector(state => state.user);
    // const dispatch = useDispatch();
    // useEffect(() => {
    //     if (!me) {
    //         dispatch({
    //             // 쿠키로 정보 불러오게
    //             type : LOAD_USER_REQUEST,
    //         });
    //     }
    // }, []);

    const {
        children
    } = props;

    return (
        <div>
            <Menu mode="horizontal">
                <Menu.Item key="home"><Link href="/"><a>nodebird</a></Link></Menu.Item>
                <Menu.Item key="profile"><Link href="/profile"><a>profile</a></Link></Menu.Item>
                <Menu.Item key="mail">
                    <Input.Search enterButton style={{verticalAlign : 'middle'}}/>
                </Menu.Item>
            </Menu>
            <Row gutter = {20}>
                <Col xs={24} md={6}>
                    {/* {me ?
                    <UserProfile/>
                    :
                    <LoginForm/>
                    } */}
                    {/* 이 파트는 2강 따라하면서 넣은 부분 */}
                    {dummy.isLoggedIn ?
                    <Card actions = {[
                        <div key="twit">짹짹<br/>{dummy.Post.length}</div>,
                        <div key="following">팔로윙<br/>{dummy.Followers.length}</div>,
                        <div key="follower">팔로워<br/>{dummy.Followings.length}</div>
                    ]}>
                    <Card.Meta
                        avatar = {<Avatar>{dummy.nickname[0]}</Avatar>}
                        title = {dummy.nickname}
                    />
                    </Card>
                    // {/* 여기 나중에 옮겨줌 userProfile로 */}
                    :
                    // {/* 여기도 2강 따라하면서 넣은 부분~ */}
                    <Form>
                        <div>
                            <label htmlFor = "user-id">ID</label>
                            <br/>
                            <Input name = "user-id"  required/>
                        </div>
                        <div>
                            <label htmlFor = "user-password">PASSWORD</label>
                            <br/>
                            <Input name = "user-password"  required/>
                        </div>
                        <Button type="primary" htmlType = "submit" loading={false}>LOGIN</Button>
                        <Link href="/signup"><a><Button>SIGN UP</Button></a></Link>
                    </Form>
                    }
                    {/* 나중에 loginform으로 옮겨줌 */}
                </Col>
                <Col xs={24} md={12}>
                    {children}
                </Col>
                <Col xs={24} md={6}>
                </Col>
            </Row>
        </div>
    )
}

AppLayout.propTypes = {
    children : PropTypes.node,
}

export default AppLayout;
